    @Test
    void getPixel() {
        for (int i = 0; i < 15; i++) {
            int rowR = 100+i+1;
            int colR = 100+i+1;
            int [][][] arr = functions.getRandomSizwOfArr(rowR,colR);
            RGBImage rgbi= new RGBImage(arr);
            for (int j = 0; j < 20; j++) {
                int row = (int)Math.floor(Math.random()*(arr[0].length+1));
                int col = (int)Math.floor(Math.random()*(arr[0][0].length+1));
                int [] arrResult = rgbi.getPixel(row,col);
                if(arrResult != null){
                    for (int k = 0; k < arrResult.length; k++) {
                        assertEquals(arrResult[k] ,row*col);
                        assertFalse(arrResult[k] ==(row*col+1));
                    }
                }else{
                    assertEquals(arrResult ,null);
                }

            }
        }
    }

